import { onMount } from "svelte"

onMount(async () => {
  (function () {
    const svg = document.getElementById('svg3d-root');
    const scene = svg.querySelector('#scene');
    const faces = Array.from(svg.querySelectorAll('.face'));

    // Parse the data-points attribute into arrays of {x,y,z}
    function parsePoints(attr) {
      return attr.trim().split(/\s+/).map(pair => {
        const nums = pair.split(',').map(Number);
        return { x: nums[0], y: nums[1], z: nums[2] };
      });
    }

    // Helper: project a 3D point (using a given projection matrix) -> returns {x,y}
    // We'll rely on svg3d library functions if present; otherwise use a simple perspective projection
    let useLib = (typeof window.svg3d !== 'undefined');
    // Library hooks if available
    let lib = window.svg3d || null;

    // Container for shape wrappers (if library provides shapeFactory)
    const wrappers = [];

    // Build per-face data structure
    faces.forEach(face => {
      const pts = parsePoints(face.getAttribute('data-points') || '');
      face._pts = pts;

      // store polygon and label DOM nodes
      face._poly = face.querySelector('polygon');
      face._label = face.querySelector('text');

      // If library provides shapeFactory, create shape wrapper for convenience
      if (useLib && lib.shapeFactory) {
        try {
          const shp = lib.shapeFactory(face);
          wrappers.push({ face, shape: shp });
        } catch (e) {
          // ignore and fall back
        }
      } else {
        wrappers.push({ face, shape: null });
      }
    });

    // Fallback perspective projection (camera at z = -600) (right-handed coords)
    function projectPoint(p, cam) {
      // rotate/translate will be applied to 3D coords externally via matrix ops
      const z = p.z - cam.z;
      const f = cam.fov / (cam.fov + z);
      return { x: p.x * f, y: p.y * f };
    }

    // If the library provides matrix helpers use them; otherwise implement simple rotation matrices
    // We'll animate angles and compute rotated 3D coords for each vertex.
    let angle = 0; // radians
    const cam = { z: 300, fov: 800 };

    function rotatePoint(p, ax, ay, az) {
      // rotate around X, then Y, then Z
      let x = p.x, y = p.y, z = p.z;
      // X
      let cosa = Math.cos(ax), sina = Math.sin(ax);
      let y1 = y * cosa - z * sina;
      let z1 = y * sina + z * cosa;
      y = y1; z = z1;
      // Y
      cosa = Math.cos(ay); sina = Math.sin(ay);
      let x2 = x * cosa + z * sina;
      let z2 = -x * sina + z * cosa;
      x = x2; z = z2;
      // Z
      cosa = Math.cos(az); sina = Math.sin(az);
      let x3 = x * cosa - y * sina;
      let y3 = x * sina + y * cosa;
      x = x3; y = y3;
      return { x, y, z };
    }

    // update function: recalculates projected 2D points and updates polygon + label positions
    function update(ax, ay, az) {
      faces.forEach(face => {
        const projected = face._pts.map(pt => {
          const rotated = rotatePoint(pt, ax, ay, az);
          const p2 = projectPoint(rotated, cam);
          // scale for viewBox units (we use direct units)
          return p2;
        });

        // polygon points attribute as "x1,y1 x2,y2 ..."
        const ptsAttr = projected.map(p => `${p.x},${p.y}`).join(' ');
        if (face._poly) face._poly.setAttribute('points', ptsAttr);

        // compute centroid for label placement (average of projected vertices)
        const cx = projected.reduce((s, p) => s + p.x, 0) / projected.length;
        const cy = projected.reduce((s, p) => s + p.y, 0) / projected.length;
        if (face._label) {
          face._label.setAttribute('x', cx);
          face._label.setAttribute('y', cy);
        }
      });
    }

    // If the svg3d lib is present and has utilities to do transforms, prefer them for more correct depth-sorting
    // We'll still use our own projection if library helpers aren't present.
    let lastTime = performance.now();
    function animate(now) {
      const dt = (now - lastTime) / 1000;
      lastTime = now;
      angle += dt * (Math.PI * 0.25); // 45 degrees per second

      // use Euler angles: rotate X and Y so cube spins nicely
      const ax = angle * 0.6; // tilt / spin
      const ay = angle * 0.9;
      const az = angle * 0.2;

      // If svg3d lib exposes higher-level API, attempt to use it for depth & better projection
      if (useLib && lib.setAnglesRotationMatrix && lib.setTranslationMatrix) {
        try {
          // Some svg3d variants want shape.transform(matrixArray) or svg3d.updatePolygons()
          const matrixRot = lib.setAnglesRotationMatrix(ax, ay, az);
          const matrixTrans = lib.setTranslationMatrix(0, 0, 0);
          const fullMatrix = [matrixRot, matrixTrans];

          let usedLibPath = false;
          wrappers.forEach(w => {
            if (w.shape && typeof w.shape.transform === 'function') {
              w.shape.transform(fullMatrix);
              usedLibPath = true;
            }
          });

          if (usedLibPath && typeof lib.updatePolygons === 'function') {
            lib.updatePolygons(); // commit to DOM if helper exists
            requestAnimationFrame(animate);
            return;
          }
          // otherwise fallthrough to our manual projection below
        } catch (e) {
          // if anything fails, fall back to manual projection
        }
      }

      // Manual projection fallback
      update(ax, ay, az);

      requestAnimationFrame(animate);
    }

    // initialize: try to call svg3d.init if present (some variants expect SVG DOM node)
    if (useLib && typeof lib.init === 'function') {
      try {
        lib.init(svg);
      } catch (e) {
        // ignore
      }
    }

    // kick animation
    requestAnimationFrame(animate);
  })();
})

