import { onMount } from 'svelte';

 let canvas;


 const size = 100; // Cube size
 const center = { x: 250, y: 250 }; // Canvas center
 let angleX = 0; // Rotation angle on X axis
 let angleY = 0; // Rotation angle on Y axis

 // Cube vertices in 3D space
 const cubeVertices = [
   [-size, -size, -size], // Front-bottom-left
   [size, -size, -size],  // Front-bottom-right
   [size, size, -size],   // Front-top-right
   [-size, size, -size],  // Front-top-left
   [-size, -size, size],  // Back-bottom-left
   [size, -size, size],   // Back-bottom-right
   [size, size, size],    // Back-top-right
   [-size, size, size]    // Back-top-left
 ];

 // Cube edges connecting the vertices
 const cubeEdges = [
   [0, 1], [1, 2], [2, 3], [3, 0], // front face
   [4, 5], [5, 6], [6, 7], [7, 4], // back face
   [0, 4], [1, 5], [2, 6], [3, 7]  // connecting edges
 ];

 // Rotate around the X axis
 function rotateX(vertex, angle) {
   const [x, y, z] = vertex;
   const cos = Math.cos(angle);
   const sin = Math.sin(angle);
   return [x, y * cos - z * sin, y * sin + z * cos];
 }

 // Rotate around the Y axis
 function rotateY(vertex, angle) {
   const [x, y, z] = vertex;
   const cos = Math.cos(angle);
   const sin = Math.sin(angle);
   return [x * cos + z * sin, y, -x * sin + z * cos];
 }

 // Project 3D points to 2D (for canvas drawing)
 function project(vertex) {
   const [x, y, z] = vertex;
   const scale = 200 / (z + 300); // Perspective effect
   return [x * scale + center.x, y * scale + center.y];
 }

 // Draw the cube on the canvas
 function drawCube() {
   const ctx = canvas.getContext('2d');
   ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear the canvas
   ctx.beginPath();

   // Rotate and project all cube vertices
   const rotatedVertices = cubeVertices.map(vertex => {
     let rotated = rotateX(vertex, angleX);
     rotated = rotateY(rotated, angleY);
     return project(rotated);
   });

   // Draw the cube edges
   cubeEdges.forEach(([start, end]) => {
     const [x1, y1] = rotatedVertices[start];
     const [x2, y2] = rotatedVertices[end];
     ctx.moveTo(x1, y1);
     ctx.lineTo(x2, y2);
   });

   ctx.strokeStyle = 'white';
   ctx.lineWidth = 2;
   ctx.stroke();
 }

 // Animation loop
 onMount(() => {
   function animate() {
     angleX += 0.01;
     angleY += 0.01;
     drawCube();
     requestAnimationFrame(animate);
   }
   animate();
 });

