@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");


#page {
  font-family: 'Georgia', serif;
  color: var(--dark-text);
  line-height: 1.8; 
  font-size: 1rem;
  background-color: var(--off-white);
}

:root {
  --beige: #E8DCC4;
  --sage: #9CAF88;
  --off-white: #F5F5F0;
  --soft-orange: #E8956B;
  --dark-text: #3A3A3A;
}

.section-container {
  max-width: var(--max-width, 1450px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

 p {
  font-size: 1.15rem;
  line-height: 2;
  color: var(--dark-text);
  opacity: 0.8;
  margin-bottom: 1.8rem;
}

.quote-block {
  background: white;
  padding: 3rem;
  margin-bottom: 3rem;
  border-left: 6px solid var(--soft-orange);
  position: relative;
}

.quote-block::before {
  content: '"';
  position: absolute;
  top: 1rem;
  left: 1rem;
  font-size: 6rem;
  color: var(--soft-orange);
  opacity: 0.15;
  font-family: Georgia, serif;
  line-height: 1;
}

.quote-block p {
  font-size: 1.5rem;
  line-height: 1.8;
  font-style: italic;
  margin: 0;
  position: relative;
  z-index: 1;
}

p{
  font-size: 1.15rem;
  line-height: 2;
  color: var(--dark-text);
  opacity: 0.8;
  margin-bottom: 1.8rem;
}
.problem-solution {
  display: grid;
  grid-template-columns: 1fr;
  gap: 3rem;
  margin: 4rem 0;
}

.problem-box, .solution-box {
  padding: 2.5rem;
  border-radius: 0;
}

.problem-box {
  background: linear-gradient(135deg, rgba(232, 149, 107, 0.1), rgba(232, 149, 107, 0.05));
  border-left: 4px solid var(--soft-orange);
}

.solution-box {
  background: linear-gradient(135deg, rgba(156, 175, 136, 0.1), rgba(156, 175, 136, 0.05));
  border-left: 4px solid var(--sage);
}

.problem-box h3, .solution-box h3 {
  font-size: 1.6rem;
  margin-bottom: 1.2rem;
  color: var(--dark-text);
  font-weight: 400;
}

.problem-box p, .solution-box p {
  font-size: 1.05rem;
  line-height: 1.9;
}

.highlight-box {
  background: linear-gradient(135deg, var(--beige), var(--off-white));
  padding: 3rem;
  margin: 3rem 0;
  border-radius: 0;
  border-left: 6px solid var(--soft-orange);
}

 h2 {
  font-size: 2.8rem;
  margin-bottom: 2rem;

  color: var(--dark-text);
  font-weight: 300;
  letter-spacing: -1px;
}
.highlight-box h3 {
  font-size: 1.8rem;
  margin-bottom: 1.5rem;
  color: var(--dark-text);
  font-weight: 400;
}

.highlight-box p {
  margin-bottom: 1.2rem;
}

.features-list li {
  padding: 1.2rem 0;
  border-bottom: 1px solid rgba(58, 58, 58, 0.1);
  font-size: 1.15rem;
  position: relative;
  padding-left: 2rem;
}

.features-list li::before {
  content: '→';
  position: absolute;
  left: 0;
  color: var(--soft-orange);
  font-weight: 700;
}

 h2 {
  font-size: 2.8rem;
  margin-bottom: 2rem;
  margin-top: 4rem;
  color: var(--dark-text);
  font-weight: 300;
  letter-spacing: -1px;
}

 h2:first-of-type {
  margin-top: 0;
}

 p {
  font-size: 1.15rem;
  line-height: 2;
  color: var(--dark-text);
  opacity: 0.8;
  margin-bottom: 1.8rem;
}

h2{
  font-size: 2.8rem;
  margin-bottom: 2rem;
  color: var(--dark-text);
  font-weight: 300;
  letter-spacing: -1px;
}
.coverage-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 2rem;
  margin: 3rem 0;
}
p{
  font-size: 1.15rem;
  line-height: 2;
  color: var(--dark-text);
  opacity: 0.8;
  margin-bottom: 1.8rem;
}
.coverage-item {
  background: white;
  padding: 2rem;
  border-left: 3px solid var(--sage);
  transition: all 0.3s ease;
}

.coverage-item:hover {
  transform: translateX(5px);
  border-left-width: 6px;
}

.coverage-item h4 {
  font-size: 1.3rem;
  margin-bottom: 0.8rem;
  color: var(--dark-text);
  font-weight: 400;
}

.coverage-item p {
  font-size: 1rem;
  line-height: 1.8;
}

.cta-section {
    text-align: center;
}

.cta-section h2 {
    font-size: 2.5rem;
    margin-bottom: 1.5rem;
    color: var(--dark-text);
    font-weight: 300;
}

.cta-section p {
    font-size: 1.2rem;
    margin-bottom: 2.5rem;
    color: var(--dark-text);
    opacity: 0.8;
}

.cta-button {
    display: inline-block;
    padding: 1.2rem 3rem;
    background: transparent;
    color: var(--dark-text);
    text-decoration: none;
    border: 2px solid var(--dark-text);
    font-weight: 400;
    transition: all 0.4s ease;
    letter-spacing: 1px;
    font-size: 1rem;
    position: relative;
    overflow: hidden;
}

.cta-button::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: var(--soft-orange);
    transition: all 0.4s ease;
    z-index: -1;
}

.cta-button:hover::before {
    left: 0;
}

.cta-button:hover {
    color: white;
    border-color: var(--soft-orange);
}
@media (max-width: 1024px) {
  .cta-section h2 {
    font-size: 2.1rem;
  }

  .cta-section p {
    font-size: 1.1rem;
  }

  .cta-button {
    padding: 1.1rem 2.5rem;
    font-size: 0.95rem;
  }
}

@media (max-width: 768px) {
  .cta-section h2 {
    font-size: 1.8rem;
  }

  .cta-section p {
    font-size: 1.05rem;
    margin-bottom: 2rem;
  }

  .cta-button {
    padding: 1rem 2.2rem;
    font-size: 0.9rem;
  }
}

@media (max-width: 480px) {
  .cta-section h2 {
    font-size: 1.6rem;
    line-height: 1.25;
  }

  .cta-section p {
    font-size: 1rem;
  }

  .cta-button {
    padding: 0.9rem 2rem;
    font-size: 0.85rem;
    letter-spacing: 0.8px;
  }
}

.intro-text {
  font-size: 1.3rem;
  line-height: 2.1;
  color: var(--dark-text);
  opacity: 0.85;
  margin-bottom: 3rem;
  padding: 3rem;
  background: white;
  border-left: 4px solid var(--sage);
}

.content-section h2 {
  font-size: 2.8rem;
  margin-bottom: 2rem;
  margin-top: 4rem;
  color: var(--dark-text);
  font-weight: 300;
  letter-spacing: -1px;
}

.content-section h2:first-of-type {
  margin-top: 0;
}

.content-section p {
  font-size: 1.15rem;
  line-height: 2;
  color: var(--dark-text);
  opacity: 0.8;
  margin-bottom: 1.8rem;
}

.highlight-box {
  background: linear-gradient(135deg, var(--beige), var(--off-white));
  padding: 3rem;
  margin: 3rem 0;
  border-radius: 0;
  border-left: 6px solid var(--soft-orange);
}

.highlight-box h3 {
  font-size: 1.8rem;
  margin-bottom: 1.5rem;
  color: var(--dark-text);
  font-weight: 400;
}

.highlight-box p {
  margin-bottom: 1.2rem;
}

.coverage-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 2rem;
  margin: 3rem 0;
}

.coverage-item {
  background: white;
  padding: 2rem;
  border-left: 3px solid var(--sage);
  transition: all 0.3s ease;
}

.coverage-item:hover {
  transform: translateX(5px);
  border-left-width: 6px;
}

.coverage-item h4 {
  font-size: 1.3rem;
  margin-bottom: 0.8rem;
  color: var(--dark-text);
  font-weight: 400;
}

.coverage-item p {
  font-size: 1rem;
  line-height: 1.8;
}

.features-list {
  list-style: none;
  margin: 2rem 0;
}

.features-list li {
  padding: 1.2rem 0;
  border-bottom: 1px solid rgba(58, 58, 58, 0.1);
  font-size: 1.15rem;
  position: relative;
  padding-left: 2rem;
}

.features-list li::before {
  content: '→';
  position: absolute;
  left: 0;
  color: var(--soft-orange);
  font-weight: 700;
}

@media (max-width: 1024px) {
  .content-section h2 {
    font-size: 2.3rem;
  }

  .intro-text {
    padding: 2.2rem;
  }
}

@media (max-width: 768px) {

  .intro-text {
    font-size: 1.15rem;
    padding: 2rem;
    margin-bottom: 2.5rem;
  }

  .content-section h2 {
    font-size: 2rem;
    margin-top: 3rem;
  }

  .content-section p {
    font-size: 1.05rem;
    line-height: 1.9;
  }

  .highlight-box {
    padding: 2rem;
    margin: 2rem 0;
  }

  .highlight-box h3 {
    font-size: 1.5rem;
  }

  .coverage-grid {
    grid-template-columns: 1fr;
    gap: 1.5rem;
  }

  .coverage-item {
    padding: 1.5rem;
  }
}


@media (max-width: 480px) {

  .intro-text {
    font-size: 1rem;
    padding: 1.5rem;
  }

  .content-section h2 {
    font-size: 1.7rem;
    line-height: 1.3;
  }

  .highlight-box {
    padding: 1.5rem;
  }

  .coverage-item h4 {
    font-size: 1.15rem;
  }

  .features-list li {
    font-size: 1rem;
    padding: 1rem 0 1rem 1.6rem;
  }

  .features-list li::before {
    font-size: 1rem;
    left: 0;
  }
}

.hero-service::before {
  content: attr(data-number);
  position: absolute;
  top: -50px;
  right: 10%;
  font-size: 20rem;
  font-weight: 700;
  opacity: 0.05;
  font-family: 'Segoe UI', sans-serif;
}

.hero-service-content {
  z-index: 1;
}

.service-label {
  font-size: 0.9rem;
  letter-spacing: 3px;
  text-transform: uppercase;
  color: var(--sage);
  margin-bottom: 1.5rem;
  font-family: 'Segoe UI', sans-serif;
}

.hero-service h1 {
  font-size: 4.5rem;
  margin-bottom: 2rem;
  color: var(--dark-text);
  font-weight: 300;
  line-height: 1.1;
  letter-spacing: -2px;
}

.hero-service .subtitle {
  font-size: 1.4rem;
  line-height: 1.9;
  color: var(--dark-text);
  opacity: 0.85;
  margin-bottom: 2.5rem;
}

.tags {
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
}

.tag {
  padding: 0.5rem 1.2rem;
  background: white;
  border-radius: 50px;
  font-size: 0.85rem;
  color: var(--dark-text);
  font-family: 'Segoe UI', sans-serif;
  letter-spacing: 0.5px;
}

@media (max-width: 1024px) {
  .hero-service::before {
    font-size: 14rem;
    top: -40px;
  }

  .hero-service h1 {
    font-size: 3.5rem;
  }

  .hero-service .subtitle {
    font-size: 1.2rem;
  }
}

@media (max-width: 768px) {
  .hero-service::before {
    font-size: 10rem;
    top: -30px;
    right: 5%;
  }

  .service-label {
    font-size: 0.8rem;
    letter-spacing: 2px;
  }

  .hero-service h1 {
    font-size: 2.8rem;
    letter-spacing: -1px;
  }

  .hero-service .subtitle {
    font-size: 1.1rem;
    line-height: 1.7;
  }

  .tags {
    gap: 0.7rem;
  }

  .tag {
    font-size: 0.8rem;
    padding: 0.45rem 1rem;
  }
}

@media (max-width: 480px) {
  .hero-service::before {
    font-size: 6rem;
    top: -20px;
    right: 0;
  }

  .service-label {
    font-size: 0.75rem;
    letter-spacing: 1.5px;
  }

  .hero-service h1 {
    font-size: 2.1rem;
    line-height: 1.15;
  }

  .hero-service .subtitle {
    font-size: 1rem;
    margin-bottom: 2rem;
  }

  .tags {
    gap: 0.6rem;
  }

  .tag {
    font-size: 0.75rem;
    padding: 0.4rem 0.9rem;
  }
}

.floating-shapes {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 0;
    overflow: hidden;
}

.shape {
    position: absolute;
    opacity: 0.15;
    animation: float 20s infinite ease-in-out;
}

.shape-1 {
    width: 300px;
    height: 300px;
    background: var(--sage);
    border-radius: 63% 37% 54% 46% / 55% 48% 52% 45%;
    top: 10%;
    left: -100px;
    animation-delay: 0s;
}

.shape-2 {
    width: 250px;
    height: 250px;
    background: var(--soft-orange);
    border-radius: 41% 59% 42% 58% / 45% 60% 40% 55%;
    top: 60%;
    right: -80px;
    animation-delay: 5s;
}

.shape-3 {
    width: 200px;
    height: 200px;
    background: var(--beige);
    border-radius: 70% 30% 30% 70% / 60% 40% 60% 40%;
    top: 80%;
    left: 10%;
    animation-delay: 10s;
}

@keyframes float {
    0%, 100% { transform: translate(0, 0) rotate(0deg); }
    33% { transform: translate(30px, -30px) rotate(120deg); }
    66% { transform: translate(-20px, 20px) rotate(240deg); }
}

.philosophy-section {
  background: linear-gradient(135deg, var(--sage), var(--beige));
  padding: 10rem 5%;
  position: relative;
  overflow: hidden;
}

.philosophy-content {

  margin: 0 auto;
  text-align: center;
  position: relative;
  z-index: 1;
}

.philosophy-section h2 {
  font-size: 3.5rem;
  margin-bottom: 2.5rem;
  color: var(--dark-text);
  font-weight: 300;
  line-height: 1.3;
}

.philosophy-section p {
  font-size: 1.3rem;
  line-height: 2.2;
  color: var(--dark-text);
  opacity: 0.85;
  margin-bottom: 2rem;
}

.stats {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 3rem;
  margin-top: 5rem;
}

.stat-item {
  text-align: center;
}

.stat-number {
  font-size: 3.5rem;
  font-weight: 300;
  color: var(--dark-text);
  margin-bottom: 0.5rem;
}

.stat-label {
  font-size: 1rem;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--dark-text);
  opacity: 0.7;
}

 @media (max-width: 1024px) {
 .stats { grid-template-columns: 1fr; }
 }

footer {
    background-color: var(--dark-text);
    color: var(--off-white);
    padding: 5rem 5% 3rem;
    position: relative;
    z-index: 1;
}

.footer-content {
    max-width: 1400px;
    margin: 0 auto;
    display: grid;
    grid-template-columns: 2fr 1fr 1fr;
    gap: 4rem;
    margin-bottom: 3rem;
}

.footer-brand h3 {
    font-size: 1.8rem;
    margin-bottom: 1rem;
    font-weight: 400;
    font-style: italic;
}

.footer-brand p {
    opacity: 0.7;
    line-height: 1.9;
}

.footer-links h4 {
    font-size: 1rem;
    letter-spacing: 2px;
    text-transform: uppercase;
    margin-bottom: 1.5rem;
    font-weight: 400;
}

.footer-links a {
    display: block;
    color: var(--off-white);
    text-decoration: none;
    opacity: 0.7;
    margin-bottom: 0.8rem;
    transition: all 0.3s ease;
}

.footer-links a:hover {
    opacity: 1;
    transform: translateX(5px);
}

.footer-bottom {
    text-align: center;
    padding-top: 2rem;
    border-top: 1px solid rgba(255,255,255,0.1);
    opacity: 0.6;
}

@media (max-width: 1024px) {
  footer {
    padding: 4rem 5% 2.5rem;
  }

  .footer-content {
    grid-template-columns: 1.5fr 1fr 1fr;
    gap: 3rem;
  }

  .footer-brand h3 {
    font-size: 1.6rem;
  }
}

@media (max-width: 768px) {
  footer {
    padding: 3.5rem 6% 2.5rem;
  }

  .footer-content {
    grid-template-columns: 1fr 1fr;
    gap: 2.5rem;
  }

  .footer-brand {
    grid-column: span 2;
  }

  .footer-brand h3 {
    font-size: 1.5rem;
  }

  .footer-links h4 {
    font-size: 0.95rem;
  }
}

@media (max-width: 480px) {
  footer {
    padding: 3rem 7% 2rem;
  }

  .footer-content {
    grid-template-columns: 1fr;
    gap: 2rem;
  }

  .footer-brand h3 {
    font-size: 1.4rem;
  }

  .footer-links a {
    margin-bottom: 0.7rem;
  }

  .footer-bottom {
    font-size: 0.85rem;
  }
}

.services {
  padding: 8rem 5%;
  max-width: 1400px;
  margin: 0 auto;
  position: relative;
  z-index: 1;
}

.section-header {
  margin-bottom: 5rem;
}

.section-title {
  font-size: 3.5rem;
  color: var(--dark-text);
  font-weight: 300;
  line-height: 1.2;
}

.section-description {
  font-size: 1.1rem;
  color: var(--dark-text);
  opacity: 0.7;
  line-height: 2;
}

.service-layout {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 3rem;
}

.service-card {
  background: white;
  padding: 3rem;
  border-radius: 0;
  transition: all 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  border-left: 3px solid transparent;
  position: relative;
  min-height: 320px;
  display: flex;
  flex-direction: column;
}

.service-card::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 0;
  height: 100%;
  background: linear-gradient(135deg, var(--beige), var(--off-white));
  transition: width 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  z-index: -1;
}

.service-card:hover::after {
  width: 100%;
}

.service-card:nth-child(1) { border-left-color: var(--sage); }
.service-card:nth-child(2) { border-left-color: var(--soft-orange); }
.service-card:nth-child(3) { border-left-color: var(--beige); }
.service-card:nth-child(4) { border-left-color: #C4A77D; }

.service-card:hover {
  transform: translateX(10px);
  border-left-width: 6px;
}

.service-number {
  font-size: 4rem;
  opacity: 0.1;
  font-weight: 700;
  margin-bottom: -2rem;
  font-family: 'Segoe UI', sans-serif;
}

.service-card h3 {
  font-size: 1.8rem;
  margin-bottom: 1.5rem;
  color: var(--dark-text);
  font-weight: 400;
}

.service-card p {
  color: var(--dark-text);
  opacity: 0.75;
  line-height: 1.9;
  flex-grow: 1;
}

.service-link {
  color: var(--soft-orange);
  text-decoration: none;
  font-size: 0.9rem;
  letter-spacing: 1px;
  margin-top: 1.5rem;
  display: inline-block;
  transition: all 0.3s ease;
}

.service-link:hover {
  transform: translateX(5px);
}

.hero {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 8rem 5%;
    position: relative;
    z-index: 1;
}

.hero-content {
    text-align: left;
}

.hero-subtitle {
    font-size: 1rem;
    letter-spacing: 3px;
    text-transform: uppercase;
    color: var(--sage);
    margin-bottom: 1.5rem;
    font-family: 'Segoe UI', sans-serif;
}

.hero h1 {
    font-size: 5rem;
    margin-bottom: 2rem;
    color: var(--dark-text);
    font-weight: 300;
    line-height: 1.2;
    letter-spacing: -2px;
}

.hero h1 .highlight {
    font-style: italic;
    color: var(--soft-orange);
    position: relative;
    display: inline-block;
}

.hero h1 .highlight::after {
    content: '';
    position: absolute;
    bottom: 10px;
    left: 0;
    width: 100%;
    height: 3px;
    background: var(--soft-orange);
    opacity: 0.3;
}

.hero p {
    font-size: 1.3rem;
    margin-bottom: 3rem;
    color: var(--dark-text);
    opacity: 0.8;
    max-width: 700px;
    line-height: 2;
}

.cta-group {
    display: flex;
    gap: 1.5rem;
}

.cta-button {
    display: inline-block;
    padding: 1rem 2.5rem;
    background: transparent;
    color: var(--dark-text);
    text-decoration: none;
    border: 2px solid var(--dark-text);
    font-weight: 400;
    transition: all 0.4s ease;
    letter-spacing: 1px;
    font-size: 0.95rem;
    position: relative;
    overflow: hidden;
}

.cta-button::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: var(--soft-orange);
    transition: all 0.4s ease;
    z-index: -1;
}

.cta-button:hover::before {
    left: 0;
}

.cta-button:hover {
    color: white;
    border-color: var(--soft-orange);
}

.cta-button.secondary {
    border-color: var(--sage);
}

.cta-button.secondary::before {
    background: var(--sage);
}

.cta-button.secondary:hover {
    border-color: var(--sage);
}

header {
  background: var(--off-white);
  width: 100%;
  z-index: 100;
  transition: all 0.3s ease;
}

header.scrolled {
  background: rgba(232, 220, 196, 0.95);
  backdrop-filter: blur(10px);
  box-shadow: 0 2px 20px rgba(0,0,0,0.08);
}

nav {
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: relative;
  z-index: 1;
}

.logo {
  font-size: 1.8rem;
  font-weight: 400;
  color: var(--dark-text);
  letter-spacing: 2px;
  font-style: italic;
}

.nav-links {
  display: flex;
  gap: 3rem;
}

.nav-links a {
  text-decoration: none;
  color: var(--dark-text);
  font-weight: 400;
  font-size: 0.95rem;
  letter-spacing: 1px;
  transition: all 0.3s ease;
  position: relative;
}

.nav-links a::before {
  content: '';
  position: absolute;
  bottom: 0;
  left: 50%;
  width: 0;
  height: 1px;
  background: var(--soft-orange);
  transition: all 0.3s ease;
  transform: translateX(-50%);
}

.nav-links a:hover::before {
  width: 100%;
}
