@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

:root {
  --primary-orange: #FF6B35;
  --primary-purple: #9D4EDD;
  --primary-teal: #06D6A0;
  --secondary-orange: #F7931E;
  --secondary-purple: #C77DFF;
  --secondary-teal: #0B7A75;
  --accent-yellow: #FCA311;
  --bg-dark: #0a0a0a;
  --bg-card: #1a1a1a;
  --bg-card-darker: #0f0f0f;
  --text-white: #ffffff;
  --text-gray: #b0b0b0;
  --border-subtle: rgba(255, 255, 255, 0.05);
}

#page {
  font-family: 'Space Grotesk', sans-serif;
            background: var(--bg-dark);
            color: var(--text-white);
            overflow-x: hidden;
}

.section-container {
  max-width: var(--max-width, 1400px);
  margin: 0 auto;
  padding: 3rem var(--padding, 1rem); 
}

.heading {
  font-size: 3rem;
  line-height: 1;
  font-weight: 700;
  margin: 0;
}

.button {
  color: white;
  background: var(--color-accent);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition);

  &:hover {
    box-shadow: 0 0 10px 5px rgba(0, 0, 0, 0.1);
  } 

  &.inverted {
    background: transparent; 
    color: var(--color-accent); 
    border: 2px solid var(--color-accent);
  }
}

.section-header {
    text-align: center;
    margin-bottom: 4rem;
}

.section-header h2 {
    font-size: clamp(2rem, 5vw, 3rem);
    margin-bottom: 1rem;
    background: linear-gradient(135deg, var(--text-white), var(--text-gray));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.section-header .accent-line {
    width: 80px;
    height: 4px;
    background: linear-gradient(90deg, var(--primary-orange), var(--primary-purple), var(--primary-teal));
    margin: 1rem auto;
    border-radius: 2px;
}

.hero-background {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 0;
}

.animated-grid {
    position: fixed;
    width: 100%;
    height: 100%;
    background: 
        linear-gradient(90deg, var(--border-subtle) 1px, transparent 1px),
        linear-gradient(0deg, var(--border-subtle) 1px, transparent 1px);
    background-size: 50px 50px;
    animation: gridMove 20s linear infinite;
    opacity: 0.3;
}

@keyframes gridMove {
    0% { transform: translate(0, 0); }
    100% { transform: translate(50px, 50px); }
}

.floating-elements {
    position: absolute;
    width: 100%;
    height: 100%;
}

.float-item {
    position: absolute;
    width: 4px;
    height: 4px;
    border-radius: 50%;
    animation: float 15s infinite ease-in-out;
}

.float-item:nth-child(1) { 
    background: var(--primary-orange); 
    top: 20%; 
    left: 10%;
    animation-delay: 0s;
}
.float-item:nth-child(2) { 
    background: var(--primary-purple); 
    top: 60%; 
    left: 80%;
    animation-delay: 2s;
}
.float-item:nth-child(3) { 
    background: var(--primary-teal); 
    top: 40%; 
    left: 30%;
    animation-delay: 4s;
}
.float-item:nth-child(4) { 
    background: var(--accent-yellow); 
    top: 70%; 
    left: 60%;
    animation-delay: 6s;
}

@keyframes float {
    0%, 100% { transform: translate(0, 0) scale(1); opacity: 0.3; }
    25% { transform: translate(50px, -50px) scale(2); opacity: 0.8; }
    50% { transform: translate(-30px, 40px) scale(1.5); opacity: 0.5; }
    75% { transform: translate(40px, 30px) scale(2.5); opacity: 0.9; }
}

.shape {
  position: fixed;
  border-radius: 50%;
  filter: blur(120px);
  opacity: 0.5;
  animation: float 20s infinite ease-in-out;
}

.shape1 {
  width: 400px;
  height: 400px;
  background: #FF6B35;
  top: 10%;
  left: 10%;
  animation-delay: 1s;
}

.shape2 {
  width: 300px;
  height: 300px;
  background: #9D4EDD;
  bottom: 20%;
  right: 15%;
  animation-delay: 5s;
}

.shape3 {
  width: 350px;
  height: 350px;
  background: #06D6A0;
  top: 50%;
  left: 30%;
  animation-delay:3s;
}

@keyframes float {
  0%, 100% { transform: translate(0, 0) rotate(0deg); }
  25% { transform: translate(50px, -50px) rotate(90deg); }
  50% { transform: translate(-30px, 30px) rotate(180deg); }
  75% { transform: translate(30px, 50px) rotate(270deg); }
}

.divider {
  height: 2px;
  background: linear-gradient(90deg, transparent,var(--primary-orange), var(--primary-purple), var(--primary-teal),transparent);
  margin: 4rem 0;
}

.section-intro p {
  text-align:center;
  text-wrap:balance;
  font-size: 1.2rem;
  color: var(--text-gray);
  line-height: 1.9;
  margin-bottom: 1.5rem;
}

.section-intro {
  text-wrap:balance;
  text-align: center;
}

.cta-section {
  text-align: center;
  padding: 6rem 2rem;
  background: linear-gradient(135deg, var(--bg-card-darker), var(--bg-card));
  border-radius: 24px;
  border: 1px solid var(--border-subtle);
  position: relative;
  overflow: hidden;
}

.cta-section::before {
  content: '';
  position: absolute;
  top: -50%;
  left: -50%;
  width: 200%;
  height: 200%;
  background: radial-gradient(circle, rgba(199, 125, 255, 0.1) 0%, transparent 70%);
  animation: rotate 20s linear infinite;
  pointer-events: none;
}

@keyframes rotate {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.cta-content {
  position: relative;
  z-index: 1;
}

.cta-section h2 {
  font-size: clamp(2rem, 5vw, 3.5rem);
  margin-bottom: 1.5rem;
  background: linear-gradient(135deg, var(--text-white), var(--primary-purple));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.cta-section p {
  font-size: 1.2rem;
  margin-bottom: 2rem;
  text-wrap:balance;
}

.cta-button {
  display: inline-block;
  padding: 1.2rem 3rem;
  background: linear-gradient(135deg, var(--primary-orange), var(--primary-purple));
  color: var(--text-white);
  text-decoration: none;
  border-radius: 50px;
  font-weight: 600;
  font-size: 1.1rem;
  transition: all 0.3s ease;
  border: none;
  cursor: pointer;
  box-shadow: 0 10px 30px rgba(255, 107, 53, 0.3);
}

.cta-button:hover {
  transform: translateY(-3px);
  box-shadow: 0 15px 40px rgba(255, 107, 53, 0.5);
}

.performance-section {
    background: var(--bg-card);
    border-radius: 24px;
    padding: 4rem 2rem;
    border: 1px solid var(--border-subtle);
}

.performance-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 3rem;
    margin-top: 3rem;
}

.performance-item {
    text-align: center;
}

.performance-metric {
    font-size: 3rem;
    font-weight: 800;
    background: linear-gradient(135deg, var(--primary-teal), var(--accent-yellow));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin-bottom: 0.5rem;
}

.performance-label {
    font-size: 1.1rem;
    color: var(--text-gray);
    font-weight: 500;
}
.section-intro p {
  text-align:center;
  text-wrap:balance;
  font-size: 1.2rem;
  color: var(--text-gray);
  line-height: 1.9;
  margin-bottom: 1.5rem;
}

.approach-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
  gap: 2rem;
}

.approach-card {
  background: var(--bg-card);
  padding: 2.5rem;
  border-radius: 20px;
  border: 1px solid var(--border-subtle);
  transition: all 0.4s ease;
  position: relative;
}

.approach-card::after {
  content: '';
  position: absolute;
  inset: 0;
  border-radius: 20px;
  padding: 1px;
  background: linear-gradient(135deg, var(--primary-orange), var(--primary-purple), var(--primary-teal));
  -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
  -webkit-mask-composite: xor;
  mask-composite: exclude;
  opacity: 0;
  transition: opacity 0.4s ease;
}

.approach-card:hover::after {
  opacity: 1;
}

.approach-card:hover {
  transform: translateY(-8px);
  background: var(--bg-card-darker);
  animation: shadowPulse 2s ease-in-out infinite;
}

@keyframes shadowPulse {
      0%, 100% {
        box-shadow: 0 20px 40px rgba(157, 78, 221, 0.3);
      }
      50% {
        box-shadow: 0 20px 60px rgba(157, 78, 221, 0.6);
      }
    }

.approach-icon {
  width: 60px;
  height: 60px;
  background: linear-gradient(135deg, var(--primary-orange), var(--primary-purple));
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
}

.approach-card h3 {
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: var(--primary-purple);
}

.approach-card p {
  color: var(--text-gray);
  line-height: 1.8;
  text-align:justify;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 2rem;
}

.stat-card {
    background: var(--bg-card);
    padding: 2rem;
    border-radius: 16px;
    border: 1px solid var(--border-subtle);
    text-align: center;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.stat-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 3px;
    background: linear-gradient(90deg, var(--primary-orange), var(--primary-purple), var(--primary-teal));
    transform: scaleX(0);
    transition: transform 0.3s ease;
}

.stat-card:hover::before {
    transform: scaleX(1);
}

.stat-card:hover {
    transform: translateY(-5px);
    border-color: var(--primary-teal);
}

.stat-number {
    font-size: 2.5rem;
    font-weight: 800;
    background: linear-gradient(135deg, var(--primary-orange), var(--primary-purple));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin-bottom: 0.5rem;
}

.stat-label {
    font-size: 0.95rem;
    color: var(--text-gray);
}

.content-section {
  animation: fadeIn 1s ease;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.section-intro {
  text-align: center;
  text-wrap:balance;
}

.section-intro p {
  font-size: 1.2rem;
  line-height: 1.9;
  margin-bottom: 1.25rem;
}

.hero {
    position: relative;
    min-height: 80vh;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
}


.hero-content {
    position: relative;
    z-index: 1;
    max-width: 900px;
    text-align: center;
}

.service-tag {
    display: inline-block;
    padding: 0.5rem 1.5rem;
    background: linear-gradient(135deg, var(--primary-orange), var(--primary-purple));
    border-radius: 50px;
    font-size: 0.9rem;
    font-weight: 600;
    letter-spacing: 1px;
    text-transform: uppercase;
    margin-bottom: 2rem;
    animation: fadeInUp 0.8s ease;
}

.hero-title {
    font-size: clamp(2.5rem, 8vw, 5rem);
    font-weight: 800;
    margin-bottom: 1.5rem;
    background: linear-gradient(135deg, var(--text-white), var(--text-gray));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: fadeInUp 0.8s ease 0.2s both;
}

.hero-description {
    font-size: clamp(1rem, 2vw, 1.3rem);
    color: var(--text-gray);
    margin-bottom: 2rem;
    line-height: 1.8;
    animation: fadeInUp 0.8s ease 0.4s both;
}

.tags-container {
    display: flex;
    justify-content: center;
    gap: 1rem;
    flex-wrap: wrap;
    max-width: 900px; 
    animation: fadeInUp 0.8s ease 0.6s both;
}

.tag {
    padding: 0.5rem 1rem;
    background: var(--bg-card);
    border: 1px solid var(--border-subtle);
    border-radius: 20px;
    font-size: 0.9rem;
    color: var(--text-gray);
    transition: all 0.3s ease;
}

.tag:hover {
    border-color: var(--primary-teal);
    color: var(--primary-teal);
    transform: translateY(-2px);
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

